package com.chat.tj.file;

import com.alibaba.excel.EasyExcel;
import com.alibaba.excel.ExcelWriter;
import com.alibaba.excel.write.metadata.WriteSheet;
import com.chat.tj.Test.ExcelImgExport;
import com.chat.tj.common.config.ExportConfig;
import com.chat.tj.common.excel.ExcelStyleStrategy;
import com.chat.tj.common.util.ExcelUtil;
import com.chat.tj.model.vo.ResponseVo;
import com.chat.tj.model.vo.res.UserResVO;
import com.chat.tj.service.UserService;
import io.swagger.annotations.Api;
import io.swagger.annotations.ApiOperation;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.BeanUtils;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;
import org.springframework.web.multipart.MultipartFile;
import org.springframework.web.servlet.support.ServletUriComponentsBuilder;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.File;
import java.io.IOException;
import java.io.InputStream;
import java.net.URL;
import java.nio.file.Files;
import java.nio.file.StandardCopyOption;
import java.util.*;

/**
 * @author tangjing
 * @date 2021/01/07 09:48
 */
@RestController
@RequestMapping("file/")
@Api("图片/文件接口")
@Slf4j
public class FileController {
    @Value("${file.path}")
    private String dirPath;

    @Autowired
    private UserService userService;

    @PostMapping(value = "upload")
    public ResponseVo<String> upload(MultipartFile file) {

        InputStream inputStream = null;
        try {
            inputStream = file.getInputStream();
        } catch (IOException e) {
            e.printStackTrace();
        }
        // 文件后缀
        String prefix = file.getOriginalFilename().substring(file.getOriginalFilename().lastIndexOf("."));
        String fileName = UUID.randomUUID() + prefix;
        File folder = new File(dirPath);
        // 文件夹不存在就自动创建
        if (!folder.exists()) {
            folder.mkdirs();
        }
        try {
            // 将文件上传到指定路径dirpath
            Files.copy(inputStream, new File(dirPath + fileName).toPath(), StandardCopyOption.REPLACE_EXISTING);
            // 拼接上传文件路径
            String fileDownloadUri = ServletUriComponentsBuilder.fromCurrentContextPath()
                    .path(fileName)
                    .toUriString();
            return ResponseVo.content(fileDownloadUri);
        } catch (IOException e) {
            e.printStackTrace();
            return ResponseVo.failed("上传失败，系统异常");
        }

    }

    @PostMapping("/export")
    @ApiOperation("不用模板导出")
    public void exportData(String userName, HttpServletResponse response, HttpServletRequest request) throws IOException {
        List<UserResVO> list = userService.findUserList(userName);
        String fileName = "聊天导出数据测试" + System.currentTimeMillis();
        // 设置导出excel响应头样式
        ExcelUtil.setExcelResponse(request, response, fileName);
        // 写法1,只有一个sheet页面导出
        // EasyExcel.write(response.getOutputStream(), UserResVO.class).sheet("测试").doWrite(list);

        // 写法2，多sheet页面导出
        ExcelWriter writer = null;
        // 主要设置excel导出使用默认样式
        writer = EasyExcel.write(response.getOutputStream()).registerWriteHandler(new ExcelStyleStrategy()).build();
        WriteSheet sheet1 = EasyExcel.writerSheet(0, "导出数据1").head(UserResVO.class).build();
        writer.write(list, sheet1);
        WriteSheet sheet2 = EasyExcel.writerSheet(1, "导出数据2").head(UserResVO.class).build();
        writer.write(list, sheet2);
        writer.finish();
    }

    @PostMapping("/exportWithTemplate")
    @ApiOperation("使用模板填充导出(带样式)")
    public void exportData2(String userName, HttpServletResponse response, HttpServletRequest request) throws IOException {
        List<UserResVO> list = userService.findUserList(userName);

        String fileName = "chatUserExport";
        // 设置导出excel表响应头样式
        ExcelUtil.setExcelResponse(request, response, fileName);
        // 获取模板
        String template = ExportConfig.TEMPLATE_PATH + ExportConfig.TEMPLATE_NAME;
        InputStream templateInputStream = this.getClass().getResourceAsStream(template);
        // 写法1,只有一个sheet页面导出
        // EasyExcel.write(response.getOutputStream(), UserResVO.class).withTemplate(templateInputStream).sheet().doFill(list);

        // 写法2，多sheet页面导出
        ExcelWriter writer = null;
        writer = EasyExcel.write(response.getOutputStream()).withTemplate(templateInputStream).build();
        WriteSheet sheet1 = EasyExcel.writerSheet(0, "导出数据1").build();
        Map<String, Object> map = new HashMap<>();
        map.put("date", ExcelUtil.getNowTime());
        // 填充一些注释的特殊数据
        writer.fill(map, sheet1);
        writer.fill(list, sheet1);

        WriteSheet sheet2 = EasyExcel.writerSheet(1, "导出数据2").build();
        writer.fill(list, sheet2);
        writer.finish();
    }

    @PostMapping("/exportImg")
    @ApiOperation("导出excel包含图片")
    public void exportImg(HttpServletResponse response, HttpServletRequest request) throws IOException {
        List<ExcelImgExport> exports = new ArrayList<>();
        ExcelImgExport vo = new ExcelImgExport();
        ExcelImgExport vo1 = new ExcelImgExport();
        vo.setName("图片1");
        vo.setUrl(new URL("https://ss1.bdstatic.com/70cFuXSh_Q1YnxGkpoWK1HF6hhy/it/u=3644257219,975333302&fm=26&gp=0.jpg"));
        vo.setString("");
        exports.add(vo);
        BeanUtils.copyProperties(vo, vo1);
        vo1.setName("图片2");
        exports.add(vo1);

        String fileName = "图片测试导出" + System.currentTimeMillis();
        // 设置导出excel响应头样式
        ExcelUtil.setExcelResponse(request, response, fileName);
        // 直接导出
        // EasyExcel.write(response.getOutputStream(), ExcelImgExport.class).sheet("测试").doWrite(exports);

        // 使用模板
        String template1 = ExportConfig.TEMPLATE_PATH + ExportConfig.EXCEL_TEMPLATE;
        InputStream templateInputStream = this.getClass().getResourceAsStream(template1);
        // EasyExcel.write(response.getOutputStream(), ExcelImgExport.class).withTemplate(templateInputStream).sheet().doFill(exports);
        // 写法2，多sheet页面导出
        ExcelWriter writer = null;
        writer = EasyExcel.write(response.getOutputStream(), ExcelImgExport.class).withTemplate(templateInputStream).build();
        WriteSheet sheet1 = EasyExcel.writerSheet(0, "导出数据1").build();
        writer.fill(exports, sheet1);

        WriteSheet sheet2 = EasyExcel.writerSheet(1, "导出数据2").build();
        writer.fill(exports, sheet2);
        writer.finish();
    }
}
